// Route pour recevoir les données
app.post('/endpoint', (req, res) => {
    // Réinitialiser les compteurs d'analyse à zéro
    vowelCountMap = {
        'a': 0,
        'e': 0,
        'i': 0,
        'o': 0,
        'u': 0,
        'y': 0,
    };
    consonantCount = 0;

    // Gérer les données reçues ici
    const data = req.body.data;
    console.log('Données reçues:', data);

    // Effectuer l'analyse du nouveau mot
    for (let i = 0; i < data.length; i++) {
        const char = data[i].toLowerCase();
        if (/[aeiouy]/.test(char)) { // Utilisation correcte de l'expression régulière
            vowelCountMap[char] = (vowelCountMap[char] ?? 0) + 1;
        } else if (/[a-z]/.test(char)) { // Utilisation correcte de l'expression régulière
            consonantCount++;
        }
    }

    // Envoyer une réponse au client
    res.send('Analyse terminée avec succès !');
});
